package test01;

public class Hello {

	public static int sum(int n, int m) {
		return n + m;
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 20; //변수 선언
	
		int s; //변수 선언
		
		s = sum(i,10);//sum 함수를 호출
		
			
		System.out.println(s);
		
	}

}
